def push_qq(msg):
    """
    推送消息到QQ酷推
    """
    if key == '':
        print("[注意] 未提供key，不进行推送！")
    else:
        server_url = f"https://push.xuthus.cc/send/{key}?"
        params = {
             "c": msg
        }
      
        response = requests.get(server_url, params=params)
        json_data = response.json()
        if json_data['reason'] == "操作成功":
            print(f"推送成功")
        else:
            print(f" 推送失败:鬼知道哪错了")
     
        print("QQ酷推鬼知道修改成功没")    
